Scripts.OnEnter:
if ( self.tooltipText ) then
	GameTooltip:SetOwner(self, self.tooltipOwnerPoint or "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltipText, nil, nil, nil, nil, true);
end
if ( self.tooltipRequirement ) then
	GameTooltip:AddLine(self.tooltipRequirement, 1.0, 1.0, 1.0, true);
	GameTooltip:Show();
end

if ( self.tooltipText ) then
	GameTooltip:SetOwner(self, self.tooltipOwnerPoint or "ANCHOR_RIGHT");
	GameTooltip:SetText(self.tooltipText, nil, nil, nil, nil, true);
end
if ( self.tooltipRequirement ) then
	GameTooltip:AddLine(self.tooltipRequirement, 1.0, 1.0, 1.0, true);
	GameTooltip:Show();
end

Scripts.OnEditFocusLost:
local slider = self:GetParent( );
local OnValueChangedScript = slider:GetScript( "OnValueChanged" );
if OnValueChangedScript then
    OnValueChangedScript( slider, slider:GetValue( ) );
end

Scripts.OnEnterPressed:
                local slider = self:GetParent( );
                local value = tonumber(self:GetText( ));
                local min, max = slider:GetMinMaxValues( );

                
                if( value and value >= min and value <= max ) then
                    slider:SetValue( value );
                end
                self:ClearFocus( );

Scripts.OnEnter:
if ( self:GetText( ) ) then
    GameTooltip:SetOwner(self, self.tooltipOwnerPoint or "ANCHOR_RIGHT", 0, -self:GetHeight( ));
    GameTooltip:SetText(self:GetText( ), nil, nil, nil, nil, 1);
end

Scripts.OnLoad:
self.type = CONTROLTYPE_SLIDER;
local min, max = self:GetMinMaxValues( );
getglobal( self:GetName( ) .. "Low" ):SetText( string.format("%.0f", min) );
getglobal( self:GetName( ) .. "High" ):SetText( string.format("%.0f", max) );
self:SetObeyStepOnDrag( true );

Scripts.OnValueChanged:
self.editBox:SetText( string.format("%.02f", value) );
if self.applyToFrame and self.applyToFrame.scale ~= value then
    StatusBars2_Config.doUpdate = true;
end

Scripts.OnLoad:
self.type = CONTROLTYPE_SLIDER;
local min, max = self:GetMinMaxValues( );
getglobal( self:GetName( ) .. "Low" ):SetText( string.format("%.0f%%", min * 100) );
getglobal( self:GetName( ) .. "High" ):SetText( string.format("%.0f%%", max * 100) );
self:SetObeyStepOnDrag( true );

Scripts.OnValueChanged:
self.editBox:SetText( string.format("%3d", value * 100) );

if self.applyToFrame then
    self.applyToFrame.alpha = value;
    self.applyToFrame:SetAlpha( value );
end

Scripts.OnEnter:
if( StatusBars2_Config.showHelpButton:GetChecked( ) ) then
    GameTooltip:SetOwner(self, self.tooltipOwnerPoint or "ANCHOR_BOTTOMRIGHT", 0, 0);
    GameTooltip:SetText( StatusBars2_GetLocalizedText( "STRING_ID_CONFIG_LOCK_BARS_DURING_PLAY_HELP_TEXT" ));
end

Scripts.OnLoad:
UIDropDownMenu_Initialize( self, StatusBars2_TextDisplayOptionsMenu_Initialize );
UIDropDownMenu_SetWidth(self, self:GetWidth());
UIDropDownMenu_SetButtonWidth(self, "TEXT");
self.label:SetText( StatusBars2_GetLocalizedText( "Text Display Options" ));

UIDropDownMenu_Initialize( self, StatusBars2_FontMenu_Initialize );
UIDropDownMenu_SetWidth(self, self:GetWidth());
UIDropDownMenu_SetButtonWidth(self, "TEXT");
self.label:SetText( StatusBars2_GetLocalizedText( "Text Size" ));

HybridScrollFrame_OnLoad(self);
self.update = StatusBars2_GroupOptions_AutoLayoutListUpdate;
HybridScrollFrame_CreateButtons(self, "StatusBars2_AutoLayoutListEntryButtonTemplate");

UIDropDownMenu_Initialize( self, StatusBars2_LayoutTypeMenu_Initialize );
UIDropDownMenu_SetWidth( self, self:GetWidth( ) );
UIDropDownMenu_SetButtonWidth( self, "TEXT" );

UIDropDownMenu_Initialize( self, StatusBars2_BarEnabledMenu_Initialize );
UIDropDownMenu_SetWidth(self, self:GetWidth());
UIDropDownMenu_SetButtonWidth(self, "TEXT");

self.type = CONTROLTYPE_SLIDER;
getglobal( self:GetName( ) .. "Low" ):SetText( "0" );
getglobal( self:GetName( ) .. "High" ):SetText( "100%" );
getglobal( self:GetName( ) .. "Low" ):Hide();
getglobal( self:GetName( ) .. "High" ):Hide();

UIDropDownMenu_Initialize( self, StatusBars2_PercentTextMenu_Initialize );
UIDropDownMenu_SetWidth(self, self:GetWidth());
UIDropDownMenu_SetButtonWidth(self, "TEXT");

Scripts.OnEnter:
if( StatusBars2_Config.showHelpButton:GetChecked( ) ) then
    GameTooltip:SetOwner(self, self.tooltipOwnerPoint or "ANCHOR_TOPLEFT", 0, 0);
    GameTooltip:SetText( StatusBars2_GetLocalizedText( "STRING_ID_CONFIG_AURA_FILTER_ENTRY_HELP_TEXT" ));
end

Scripts.OnTextChanged:
                        local addButton = self:GetParent( ).addEntryButton;

                        if( self:GetText( ) == "" ) then
                            addButton:Disable( );
                        else
                            addButton:Enable( );
                        end

Scripts.OnLoad:
HybridScrollFrame_OnLoad(self);
self.update = StatusBars2_BarOptions_AuraListUpdate;
HybridScrollFrame_CreateButtons(self, "StatusBars2_AuraListEntryButtonTemplate");

getglobal( self:GetName( ) .. "Text" ):SetText( StatusBars2_GetLocalizedText( "Only show auras listed" ));
getglobal( self:GetName( ) .. "Text" ):ClearAllPoints( );
getglobal( self:GetName( ) .. "Text" ):SetPoint("RIGHT", self, "LEFT");
getglobal( self:GetName( ) .. "Text" ):SetJustifyH("RIGHT");

Scripts.OnEnter:
if( StatusBars2_Config.showHelpButton:GetChecked( ) ) then
    GameTooltip:SetOwner(self, self.tooltipOwnerPoint or "ANCHOR_TOPLEFT", 0, 0);
    GameTooltip:SetText( StatusBars2_GetLocalizedText( "STRING_ID_CONFIG_AURA_FILTER_LIST_HELP_TEXT" ));
end

Scripts.OnLoad:
UIDropDownMenu_Initialize( self, StatusBars2_BarEnabledMenu_Initialize, "MENU" );
UIDropDownMenu_SetWidth(self, self:GetWidth());
UIDropDownMenu_SetButtonWidth(self, "TEXT");

StatusBars2_Config_BarSelectLabel:ClearAllPoints( );
StatusBars2_Config_BarSelectLabel:SetPoint("RIGHT", self, "LEFT", 10, 2);
UIDropDownMenu_Initialize( self, StatusBars2Config_BarSelect_Initialize );
UIDropDownMenu_SetWidth(self, self:GetWidth());
UIDropDownMenu_SetButtonWidth(self, "TEXT");

UIDropDownMenu_Initialize( self, StatusBars2_CopyFromProfileMenu_Initialize );
UIDropDownMenu_SetWidth(self, self:GetWidth());
UIDropDownMenu_SetButtonWidth(self, "TEXT");

tinsert(UISpecialFrames, self:GetName());
PanelTemplates_SetNumTabs(self, 4);
PanelTemplates_SetTab(self, 1);
StatusBars2Config_Configure_Bar_Options( self );

Scripts.OnUpdate:
                StatusBars2.hideHelp = self.showHelpButton:GetChecked( );

                if (self.doUpdate) then
                    self.doUpdate = false;
                    StatusBars2Config_OnUpdate( self );
                end
